mapper.select:
SELECT RN , BOARD_NUM , BOARD_TITLE , USER_ID , BOARD_REG_DATE , REPLY_COUNT , BOARD_READ_CNT , LIKE_COUNT , BOARD_CATE_CODE , BOARD_CATE_NAME FROM ( SELECT ROWNUM AS RN , BOARD_NUM , BOARD_TITLE , USER_ID , BOARD_REG_DATE , REPLY_COUNT , BOARD_READ_CNT , LIKE_COUNT , BOARD_CATE_CODE , BOARD_CATE_NAME FROM ( SELECT DISTINCT BO.BOARD_NUM , BOARD_TITLE , BO.USER_ID , TO_CHAR(BOARD_REG_DATE, 'YYYY-MM-DD HH24') AS BOARD_REG_DATE , (SELECT COUNT(REPLY_NUM) FROM BOARD_REPLY WHERE BOARD_NUM = BO.BOARD_NUM) AS REPLY_COUNT , BOARD_READ_CNT , (SELECT COUNT(LIKE_NUM) FROM BOARD_LIKE WHERE BOARD_NUM = BO.BOARD_NUM) AS LIKE_COUNT , CATE.BOARD_CATE_CODE , CATE.BOARD_CATE_NAME FROM BOARD BO LEFT JOIN BOARD_REPLY RE ON BO.BOARD_NUM = RE.BOARD_NUM LEFT JOIN BOARD_CATEGORY CATE ON BO.BOARD_CATE_CODE = CATE.BOARD_CATE_CODE WHERE 1 = 1
AND UPPER(BO.USER_ID) LIKE UPPER('%'||#{userId}||'%')
AND UPPER(BO.BOARD_TITLE) LIKE UPPER('%'||#{boardTitle}||'%')
AND BO.BOARD_CATE_CODE = #{boardCateCode}
AND TO_CHAR(BOARD_REG_DATE, 'YYYY-MM-DD') >= #{fromDate}
AND TO_CHAR(BOARD_REG_DATE, 'YYYY-MM-DD') <= #{toDate}
ORDER BY BOARD_NUM DESC ) )
WHERE RN >= #{startNum} AND RN <= #{endNum}

mapper.insert:
INSERT INTO BOARD (
	BOARD_NUM
	, USER_ID
	, BOARD_TITLE
	, BOARD_CONTENT
	, BOARD_CATE_CODE


) VALUES (
	(SELECT NVL(MAX(BOARD_NUM),0)+1 FROM BOARD)
	, #{userId}
	, #{boardTitle}
	, #{boardContent}
	, #{boardCateCode}

)

mapper.update:
UPDATE BOARD
SET	BOARD_TITLE = #{boardTitle}
	, BOARD_CONTENT = #{boardContent}
WHERE BOARD_NUM = #{boardNum}

UPDATE BOARD
SET BOARD_READ_CNT = BOARD_READ_CNT + 1 
WHERE BOARD_NUM = #{boardNum}
