mapper.insert:
MERGE INTO BOARD_LIKE
USING DUAL
ON (BOARD_NUM = #{boardNum} AND USER_ID = #{userId})
WHEN MATCHED THEN
	UPDATE 
    SET LIKE_NUM = LIKE_NUM
    WHERE BOARD_NUM = #{boardNum} AND USER_ID = #{userId}

	DELETE 
	WHERE BOARD_NUM = #{boardNum} AND USER_ID = #{userId}
WHEN NOT MATCHED THEN
	INSERT(
		LIKE_NUM
		, BOARD_NUM
		, USER_ID
	) VALUES (
		(SELECT NVL(MAX(LIKE_NUM), 0) + 1 FROM BOARD_LIKE)
		, #{boardNum}
		, #{userId}
	)
