mapper.select:

SELECT RESERVE_TIME 
		FROM RESERVATION_MEETING_ROOM
		WHERE ROOM_CODE = #{roomCode}
		AND RESERVE_DATE = #{reserveDate}
		AND RESERVE_AVAILABLE = 'Y'

mapper.insert:



INSERT INTO RESERVATION_MEETING_ROOM (
		RESERVE_CODE
		,ROOM_CODE
		,RESERVE_TIME
		,RESERVE_AVAILABLE



) 
VALUES (
		(SELECT 'RESERVE_' ||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(RESERVE_CODE,9))),0) +1,3,0) FROM RESERVATION_MEETING_ROOM)
		,#{roomCode}
		,#{reserveTime}
		,'N'


)
